package 浙大第一课;

import java.util.Scanner;

public class 计数循环 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner in = new Scanner(System.in);
		int count =100;
		while (count >0)
		{
			System.out.println(count);
			count = count -1;
		}
		System.out.println(count);
		System.out.println("发射");
	}

}
